<template>
  <div>
    <div class="k-field-label">
      <span class="google-icon"></span> {{ headline }}
    </div>
    <div class="google-search-preview">
      <div class="google-search-preview__content-container">
        <h2 class="google-search-preview__headline">
          {{ meta_title }}
        </h2>
        <span class="google-search-preview__url">{{ meta_url }}</span>
        <p class="google-search-preview__paragraph">
          {{ meta_description }}
        </p>
      </div>
    </div>
    <p class="google-progressheader"><span>Title:</span><span>{{titleLengthInPx}}px / {{titleMaxPxLength}}px</span></p>
    <div class="google-progress">
      <div class="google-progresscalc" v-bind:style="titleProgressStyle" v-bind:class="titleProgressClass"></div>
    </div>
    <p class="google-progressheader"><span>Description:</span><span>{{descLengthInPx}}px / {{descMaxPxLength}}px</span></p>
    <div class="google-progress">
      <div class="google-progresscalc" v-bind:style="descProgressStyle" v-bind:class="descProgressClass"></div>
    </div>
    <div
        v-if="siteTitleAsHomePageTitle && uid == 'home'"
        class="k-text k-field-help"
        data-theme="help"
    >
      <p>Using the site title as home page title.</p>
    </div>
  </div>
</template>

<script>
export default {
  data() {
    const titleMaxPxLength = 580;
    const descMaxPxLength = 990;
    const titleFontSize = 20;
    const descFontSize = 14;

    return {
      headline: null,
      site_title: null,
      this_page_title: null,
      url: null,
      meta_image: null,
      titleMaxPxLength,
      descMaxPxLength,
      titleFontSize,
      descFontSize,
    };
  },
  created: function() {
    this.load().then((response) => {
      this.headline = response.headline;
      this.page_title = response.title.value;
      this.meta_url = response.url;
      this.uid = response.uid;
      this.siteTitleAfterPageTitle = response.siteTitleAfterPageTitle;
      this.siteTitleAsHomePageTitle = response.siteTitleAsHomePageTitle;
      this.separator = response.separator;
    });
    this.$api.site.get().then((response) => {
      this.site_title = response.title;
      this.site_meta_title = response.content.meta_title;
    });
  },
  computed: {
    meta_title() {
      let meta_title = this.$store.getters["content/values"]().meta_title;
      if (this.site_title == this.page_title) {
        if (meta_title == "") {
          meta_title = this.site_title;
        }
      } else if (this.uid == "home" && this.siteTitleAsHomePageTitle) {
        meta_title = this.site_meta_title || this.site_title;
      } else {
        if (this.siteTitleAfterPageTitle == true) {
          if (meta_title == "") {
            meta_title = this.page_title + this.separator + this.site_title;
          } else {
            meta_title = meta_title + this.separator + this.site_title;
          }
        } else {
          if (meta_title == "") {
            meta_title = this.site_title + this.separator + this.page_title;
          } else {
            meta_title = this.site_title + this.separator + meta_title;
          }
        }
      }
      return meta_title;
    },
    meta_description() {
      let meta_description = this.$store.getters["content/values"]()
          .meta_description;

      return meta_description.length < 1
          ? "[Description Missing]"
          : meta_description;
    },
    store_image() {
      return this.$store.getters["content/values"]().meta_image;
    },
    titleLengthInPx() {
      return this.measureTextPxWidth(this.meta_title, this.titleFontSize);
    },
    titleProgressInPercent() {
      return this.getProgressWidthInPercent(this.titleMaxPxLength, this.titleLengthInPx);
    },
    titleProgressClass() {
      return {
        "google-progresscalc--error": this.titleProgressInPercent >= 100,
        "google-progresscalc--warning": this.titleProgressInPercent >= 80,
        "google-progresscalc--ok": this.titleProgressInPercent < 80,
      }
    },
    titleProgressStyle() {
      return {
        'width': this.titleProgressInPercent + '%'
      }
    },
    descLengthInPx() {
      if (this.$store.getters["content/values"]().meta_description.length < 1) {
        return 0;
      }
      return this.measureTextPxWidth(this.meta_description, this.descFontSize);
    },
    descProgressInPercent() {
      if (this.$store.getters["content/values"]().meta_description.length < 1) {
        return 0;
      }
      return this.getProgressWidthInPercent(this.descMaxPxLength, this.descLengthInPx);
    },
    descProgressClass() {
      return this.progressbarClass(this.descProgressInPercent);
    },
    descProgressStyle() {
      return {
        'width': this.descProgressInPercent + '%'
      }
    },
  },
  methods: {
    measureTextPxWidth(inputContent, fontSize) {
      const measurer = document.createElement("span");
      measurer.style.setProperty("all", "revert", "important");
      measurer.style.setProperty("position", "position", "important");
      measurer.style.setProperty("visibility", "hidden", "important");
      measurer.style.setProperty("width", "max-content", "important");
      measurer.style.setProperty("font-family", '"arial","sans-serif"', "important");
      measurer.style.setProperty("font-size", fontSize + 'px', "important");
      measurer.innerText = inputContent;

      document.body.appendChild(measurer);
      const { width } = measurer.getBoundingClientRect();
      document.body.removeChild(measurer);
      return Math.round(width);
    },
    getProgressWidthInPercent(maxValue, pixelSize) {
      return pixelSize * 100 / maxValue;
    },
    progressbarClass(percent) {
      if (percent >= 100) {
        return 'google-progresscalc--error';
      } else if (percent >= 80) {
        return 'google-progresscalc--warning';
      } else {
        return 'google-progresscalc--ok';
      }
    }
  },
  watch: {
    store_image: {
      handler() {
        if (this.store_image.length === 0) {
          this.meta_image = null;
        } else {
          this.$api.files
              .get(
                  this.$store.getters["content/model"]().api,
                  this.store_image[0].filename,
                  {
                    view: "compact",
                  }
              )
              .then((response) => {
                this.meta_image = response.url;
              });
        }
      },
      immediate: true,
    },
  },
};
</script>

